				var te=div.innerHTML;
				var p= te.indexOf("<!--$QGRIDTABLE_START-->");
				var pe= te.indexOf("<!--$QGRIDTABLE_END-->");
				var pstr=te.substring(0, p+ "<!--$QGRIDTABLE_START-->".length);
